from modelo.cantidad import Cantidaddef test_cantidad_puede_ser_creada_con_decimales():    # dado    cantidad = Cantidad(1234.56)    # cuando    resultado = cantidad.valor    # entonces    assert resultado == 1234.56def test_cantidad_puede_ser_creada_sin_decimales():    # dado    cantidad = Cantidad(1234)    # cuando    resultado = cantidad.valor    # entonces    assert resultado == 1234def test_cantidad_puede_ser_formateada():    # dado    cantidad = Cantidad(1357246.80)    # cuando    resultado = cantidad.como_texto()    # entonces    assert resultado == '1.357.246,80 EUR'